import React, {useState} from 'react';
import MusicOwnership from '../artifacts/MusicOwnership.json';
import Web3 from 'web3';
import detectEthereumProvider from '@metamask/detect-provider';
import styles from './CSS Modules/FileInputButtons-style.module.css';


  function ContractInteraction (props) {
    const [accounts, setAccounts] = useState([]);
    const [web3, setWeb3] = useState (undefined);
    const [contract, setContract] = useState (undefined);
    const [uri, setUri] = useState (undefined);
    const [tokenID, setTokenID] = useState (undefined);
    const [url, setUrl] = useState (undefined);


    async function init() {

      try {
          const uri = await (props.CIDkey); 
          const tokenID = await (props.tokenIDs);   
          console.log(tokenID)                
          const  web3 = await new Web3(window.ethereum);
          const accounts = await  web3.eth.getAccounts();
          const networkId = await web3.eth.net.getId();
          const deployedNetwork = MusicOwnership.networks[networkId];
          const contract = await new web3.eth.Contract(
          MusicOwnership.abi,
          deployedNetwork && deployedNetwork.address
          );
          console.log(contract)
          await setUri(uri);
          await setTokenID(tokenID);
          await setWeb3(web3);
          await setAccounts(accounts);
          await setContract(contract);
          console.log(tokenID);
          console.log(uri);
          }

      catch (error) {
        alert(`Failed to load web3, accounts or contract from the abi. Check console For details.`);
        console.error(error);
      }
    }


        async function Mint() {
            if (uri === undefined || tokenID === undefined) {
                alert('please ensure a file first and that you have entered a numerical Identification number');
                throw('please ensure a file first and that you have entered a numerical Identification number');
            }

            await contract.methods.mint(accounts[0],uri,tokenID).send({from:accounts[0], gas: 1000000});
            }

            if(typeof web3 !== 'undefined'
               && typeof accounts !== 'undefined'
               && typeof contract !== 'undefined') {
            }

            async function RetrieveTokenURL() {
            if (tokenID === undefined)
            {
              alert('please enter a numerical Identification number');
              throw('please enter a numerical Identification number');
            }

            const url = await contract.methods.tokenURI(tokenID);
            setUrl(JSON.stringify.url);
            console.log((JSON.stringify.url[tokenID]));
          }

            async function configureMoonbaseAlpha() {
                const provider = await detectEthereumProvider({
                  mustBeMetaMask: true
                })
                if (provider) {
                  try {
                    await provider.request({ method: 'eth_requestAccounts'})
                    await provider.request({
                      method: 'wallet_addEthereumChain',
                      params: [{
                        chainId: '0x3', 
                        chainName: 'Ropsten',
                        nativeCurrency: {
                          name: 'ETH',
                          symbol: 'ETH',
                          decimals: 18
                         },
                        rpcUrls: ['https://ropsten.infura.io/v3/b3b18fff97b6463ca552f9334d7fe15f'],
                        blockExplorerUrls: ['https://ropsten.etherscan.io']
                      }]
                    })
                  } catch(e) {
                    console.error(e);
                  }  
                } else {
                  console.error('Please install MetaMask');
                }
              }


  return (
  <div className={styles.column}>
        <h2>4.Submit Your Metadata and Mint your Token</h2>
  <div>
  <button onClick = {() => init()}> ShowAccount </button>
  <button onClick = {() => Mint()}> Mint Token </button>
  <button onClick={() => configureMoonbaseAlpha()}>Connect to BlockChain</button>
  <button onClick = {() => RetrieveTokenURL()}> check id</button>
  <p> {url} </p>

  </div>
  <div>
  <h3>Account</h3>
  <p> {accounts[0]} </p>
  </div>
  </div>
  );
}
export default ContractInteraction
