import React, {useState} from 'react'
import styles from './CSS Modules/FileInputButtons-style.module.css';
import ReactDOM from 'react-dom';

const PROJECT_ID = '1z2zKzXJSnmfHxjLo8APH4Ljxhl';
const PROJECT_SECRET = '796b649fad2aac594c4767a8abcd7be4';
var Cid = '';
var fileCIDAddress = 'Hash Value';

function UploadFile() 
{
  const [selectedFile, setSelectedFile] = useState();
  const [isFilePicked, setIsFilePicked] = useState(false);
  const [isSubmitted, setSubmission] = useState(false);

  const changeHandler = (event) => {
	  setSelectedFile(event.target.files[0]);
    setIsFilePicked(true);
  };

  function handleSubmission() {
	const formData = new FormData();
	formData.append('Submitted File', selectedFile);
  console.log(formData);

  const xhr =  new XMLHttpRequest();
  xhr.open(
    "POST",
    "https://ipfs.infura.io:5001/api/v0/add"
  );

  xhr.setRequestHeader(
    "Authorization", 
    "Basic" + btoa(PROJECT_ID + ":" + PROJECT_SECRET)
  );

  xhr.send(formData); 

  xhr.onload = () => {
    Cid = JSON.parse(`${xhr.response}`);
    console.log(Cid.Hash);
    fileCIDAddress = Cid.Hash;
    if (fileCIDAddress =! null)
      {
        setSubmission(true);
      }
    }  
  }



  return (
  <div >
        <h2>1. Upload An Audio Track:</h2>

    <input type="file" name="file" onChange={changeHandler} className={styles.customfileinput}/>
    {isFilePicked ? (
      <div>
				<p className={styles.FileinputText}> File Name: {selectedFile.name} </p>
			</div>
      ) : (<p className={styles.uploadFile}></p>
        )}
    <br/>

    <div>
    <button className={styles.SubmitButton} onClick={handleSubmission} > Submit File</button> 
    {isSubmitted ? 
    (<div>
      <p> Address: {'https://ipfs.io/ipfs/' + fileCIDAddress}</p>
      <p className={styles.FileinputText}>SUCCESFULLY UPLOADED</p>

    </div> 
    ) : 
    (<p className={styles.uploadFile}></p>) 
    }
    </div>
  </div>
);
    }

  export default UploadFile
